import { AfterViewChecked, Component, Input, OnInit } from '@angular/core';
import { ContenidoTarjeta2 } from '../interfaces/contenidoTarjeta2';

@Component({
  selector: 'app-card2',
  templateUrl: './card2.component.html',
  styles: [
  ]
})
export class Card2Component implements AfterViewChecked {
  @Input() cardData!: ContenidoTarjeta2;

  ngAfterViewChecked(): void {
    console.log(this.cardData);
  }

}
<div class="border border-secondary rounded-1 p-3 grow mb-3">
    <div class="row">
        <div class="col p-4">
            <img [src]="cardData.urlImg">
        </div>
        <div class="col">
            <h6>{{ cardData.titulo }}</h6>
            <p>{{ cardData.cuerpo }}</p>
            <button type="button" disabled class="btn btn-outline-secondary pt-0 pb-0" style="font-size: smaller;">{{ cardData.capacidad }}</button>
        </div>
    </div>
</div>
